mapper.select:
SELECT 

FROM cl_member a

WHERE a.id = #{id}

SELECT
FROM cl_member a
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.mobile = #{mobile}
ORDER BY ${page.orderBy}

SELECT 

FROM cl_member a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE cl_member SET 	
	name = #{name},
	mobile = #{mobile},
	level = #{level},
	balance = #{balance}
WHERE id = #{id}
