mapper.select:
SELECT 

FROM cl_base_salary_item a

WHERE a.id = #{id}

SELECT
FROM cl_base_salary_item a
AND a.user_id = #{user.id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM cl_base_salary_item a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE cl_base_salary_item SET 	
	user_id = #{user.id},
	name = #{name},
	amount = #{amount}
WHERE id = #{id}
