mapper.select:
SELECT 

FROM cl_monthly_salary_item a

WHERE a.id = #{id}

SELECT
FROM cl_monthly_salary_item a
AND a.salary_id = #{salary.id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM cl_monthly_salary_item a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE cl_monthly_salary_item SET 	
	salary_id = #{salary.id},
	name = #{name},
	amount = #{amount},
	coefficient = #{coefficient}
WHERE id = #{id}

DELETE FROM cl_monthly_salary_item


		WHERE id = #{id}


		WHERE salary_id = #{salary.id}
