mapper.select:
SELECT 

FROM cl_sale_item a

WHERE a.id = #{id}

SELECT 

FROM cl_sale_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.sale_id = #{sale.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM cl_sale_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE cl_sale_item SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE sale_id = #{sale.id}
